<?php if ($_SERVER["REQUEST_METHOD"] == "POST") { $name = mb_convert_encoding($_POST["name"], 'UTF-8', 'auto'); $lastname = mb_convert_encoding($_POST["lastname"], 'UTF-8', 'auto'); $email = $_POST["email"]; $phone = $_POST["phone"]; $message = mb_convert_encoding($_POST["message"], 'UTF-8', 'auto'); $to = "info@kiazarco.com"; $subject = "contact us - kiazar"; $body = "نام: " . $name . "\n"; $body .= "نام‌خانوادگی: " . $lastname . "\n"; $body .= "ایمیل: " . $email . "\n"; $body .= "تلفن تماس: " . $phone . "\n"; $body .= "پیام: " . $message . "\n"; $headers = "From: " . $email . "\r\n"; $headers .= "Reply-To: " . $email . "\r\n"; $headers .= "X-Mailer: PHP/" . phpversion() . "\r\n"; $headers .= 'Content-type: text/plain; charset=UTF-8' . "\r\n"; if (mail($to, $subject, $body, $headers)) { echo "ایمیل با موفقیت ارسال شد."; } else { echo "خطایی در ارسال ایمیل رخ داده است."; } } ?>